const HTTP_METHODS = ["GET", "POST"] as const;
type HttpMethod = (typeof HTTP_METHODS)[number];

const isTriggerKind = (value: NodeChangeValue): value is TriggerKind =>
	typeof value === "string" &&
	(TRIGGER_TYPES as ReadonlyArray<string>).includes(value);
const isTimerDelayType = (
	value: string,
): value is NonNullable<TimerData["delayType"]> =>
	(TIMER_DELAY_TYPES as ReadonlyArray<string>).includes(value);
const isConditionType = (
	value: string,
): value is NonNullable<ConditionData["type"]> =>
	(CONDITION_TYPES as ReadonlyArray<string>).includes(value);
const isConditionOperator = (
