): value is NonNullable<ConditionData["operator"]> =>
	(CONDITION_OPERATORS as ReadonlyArray<string>).includes(value);
const isActionKind = (
	value: NodeChangeValue,
): value is NonNullable<ActionData["type"]> =>
	typeof value === "string" &&
	(ACTION_TYPES as ReadonlyArray<string>).includes(value);
const isHttpMethod = (value: string): value is HttpMethod =>
	(HTTP_METHODS as ReadonlyArray<string>).includes(value);

const cloneTrigger = (trigger?: TriggerData): TriggerData => ({
	...(trigger ?? {}),
	config: trigger?.config ? { ...trigger.config } : undefined,
});

const cloneAction = (action?: ActionData): ActionData => ({
	...(action ?? {}),
	config: action?.config ? { ...action.config } : undefined,
});

const updateTriggerData = (
	trigger: TriggerData | undefined,
	pathParts: string[],
	value: NodeChangeValue,
): TriggerData => {
	const nextTrigger = cloneTrigger(trigger);
	const [segment, subSegment] = pathParts;
	if (!segment) {
		return nextTrigger;
	}
	if (segment === "type" && isTriggerKind(value)) {
		return { ...nextTrigger, type: value };
	}
	if (segment === "config" && subSegment) {
		if (subSegment === "keywords" && isStringArray(value)) {
			return {
				...nextTrigger,
				config: { ...(nextTrigger.config ?? {}), keywords: value },
			};
		}
		if (isTriggerConfigStringKey(subSegment) && typeof value === "string") {
			return {
				...nextTrigger,
				config: { ...(nextTrigger.config ?? {}), [subSegment]: value },
			};
		}
	}
	return nextTrigger;
};

const updateTimerData = (
	timer: TimerData | undefined,
	pathParts: string[],
	value: NodeChangeValue,
): TimerData => {
	const nextTimer: TimerData = { ...(timer ?? {}) };
	const [segment] = pathParts;
	if (!segment) {
		return nextTimer;
	}
	if (segment === "workingHoursOnly" && typeof value === "boolean") {
		return { ...nextTimer, workingHoursOnly: value };
	}
	if (typeof value !== "string") {
		return nextTimer;
	}
	switch (segment) {
		case "delayType":
			return isTimerDelayType(value)
				? { ...nextTimer, delayType: value }
				: nextTimer;
		case "hours":
			return { ...nextTimer, hours: value };
		case "minutes":
			return { ...nextTimer, minutes: value };
		case "days":
			return { ...nextTimer, days: value };
		case "time":
			return { ...nextTimer, time: value };
		case "date":
			return { ...nextTimer, date: value };
		case "dateTime":
			return { ...nextTimer, dateTime: value };
		default:
			return nextTimer;
	}
};

