{"output":"import type { Project } from "@shared/schema";
import { useQuery } from "@tanstack/react-query";
import { type ReactNode, useEffect } from "react";
import { useProject } from "@/contexts/ProjectContext";

interface ProjectInitializerProps {
	children: ReactNode;
}

export function ProjectInitializer({ children }: ProjectInitializerProps) {
	const { setCurrentProject, setProjects, setIsLoading } = useProject();

	const { data: projects, isLoading } = useQuery<Project[]>({
		queryKey: ["/api/projects"],
	});

	useEffect(() => {
		setIsLoading(isLoading);
	}, [isLoading, setIsLoading]);

	useEffect(() => {
		if (projects) {
			setProjects(projects);

			if (projects.length > 0) {
				const savedProjectId = localStorage.getItem("currentProjectId");
				if (savedProjectId) {
					const savedProject = projects.find((p) => p.id === savedProjectId);
					if (savedProject) {
						setCurrentProject(savedProject);
						return;
					}
				}

				setCurrentProject(projects[0]);
			}
		}
	}, [projects, setCurrentProject, setProjects]);

	if (isLoading) {
		return (
			<div className="flex items-center justify-center h-screen">
				<div className="text-center">
					<div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
					<p className="text-muted-foreground">Loading projects...</p>
				</div>
			</div>
		);
	}

	return <>{children}</>;
}
