			trigger: robot.trigger as TriggerData,
			onChange,
			onDelete,
			customVariables,
			projectId,
		},
	});
	yPos += 150;

	const conditions = (robot.conditions as ConditionData[]) || [];
	conditions.forEach((cond, idx) => {
		const nodeId = `condition-${idx + 1}`;
		nodes.push({
			id: nodeId,
			type: "condition",
			position: { x: 250, y: yPos },
			data: { condition: cond, onChange, onDelete, customVariables, projectId },
		});
		const prevNodeId = idx === 0 ? "trigger-1" : `condition-${idx}`;
		edges.push({
			id: `e-${prevNodeId}-${nodeId}`,
			source: prevNodeId,
			target: nodeId,
		});
		yPos += 150;
	});

	const actions = (robot.actions as ActionData[]) || [];
	actions.forEach((action, idx) => {
		const nodeId = `action-${idx + 1}`;
		nodes.push({
			id: nodeId,
			type: "action",
			position: { x: 250, y: yPos },
			data: { action, onChange, onDelete, customVariables, projectId },
		});
		const prevNodeId =
			conditions.length > 0 ? `condition-${conditions.length}` : "trigger-1";
		edges.push({
			id: `e-${prevNodeId}-${nodeId}`,
			source: prevNodeId,
			target: nodeId,
		});
		yPos += 150;
	});

	return { nodes, edges };
