const updateActionData = (
	action: ActionData | undefined,
	pathParts: string[],
	value: NodeChangeValue,
): ActionData => {
	const nextAction = cloneAction(action);
	const [segment, subSegment] = pathParts;
	if (!segment) {
		return nextAction;
	}
	if (segment === "type" && isActionKind(value)) {
		return { ...nextAction, type: value };
	}
	if (segment === "config" && subSegment) {
		if (
			subSegment === "httpMethod" &&
			typeof value === "string" &&
			isHttpMethod(value)
		) {
			return {
				...nextAction,
				config: { ...(nextAction.config ?? {}), httpMethod: value },
			};
		}
		if (
			isActionConfigKey(subSegment) &&
			subSegment !== "httpMethod" &&
			typeof value === "string"
		) {
			return {
				...nextAction,
				config: { ...(nextAction.config ?? {}), [subSegment]: value },
			};
		}
	}
	return nextAction;
};

const updateNodeData = (
	data: NodeData,
	pathParts: string[],
	value: NodeChangeValue,
): NodeData => {
	if (pathParts.length === 0) {
		return data;
	}
	const [group, ...rest] = pathParts;
	switch (group) {
		case "trigger":
			return { ...data, trigger: updateTriggerData(data.trigger, rest, value) };
		case "timer":
			return { ...data, timer: updateTimerData(data.timer, rest, value) };
		case "message":
			return { ...data, message: updateMessageData(data.message, rest, value) };
		case "condition":
			return {
				...data,
				condition: updateConditionData(data.condition, rest, value),
			};
		case "action":
			return { ...data, action: updateActionData(data.action, rest, value) };
		default:
			return data;
	}
};

