{"output":"import { useCallback, useEffect, useRef } from "react";
import { useProject } from "@/contexts/ProjectContext";
import { queryClient } from "@/lib/queryClient";

interface WebSocketMessage {
	event: string;
	data: Record<string, unknown>;
}

type WebSocketEventHandler = (message: WebSocketMessage) => void;

export function useWebSocket(onMessage?: WebSocketEventHandler) {
	const wsRef = useRef<WebSocket | null>(null);
	const handlerRef = useRef<WebSocketEventHandler | undefined>(onMessage);
	const { currentProject } = useProject();

	useEffect(() => {
		handlerRef.current = onMessage;
	}, [onMessage]);

	const connect = useCallback(() => {
		// CRITICAL SECURITY: Don't connect without a valid projectId
		if (!currentProject?.id) {
			console.log("[WS Client] Not connecting: no current project");
			return;
		}

		const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
		// CRITICAL SECURITY: Include projectId as query parameter for authentication
		const wsUrl = `${protocol}//${window.location.host}/ws?projectId=${encodeURIComponent(currentProject.id)}`;

		console.log(`[WS Client] Connecting to project: ${currentProject.id}`);
		const ws = new WebSocket(wsUrl);

		ws.onopen = () => {
			console.log("WebSocket connected");
		};

		const invalidateByPrefix = (prefix: string) => {
			queryClient.invalidateQueries({
				predicate: (query) => {
					const key = query.queryKey[0];
					return typeof key === "string" && key.startsWith(prefix);
				},
			});
		};

		const invalidateConversationMessages = (conversationId: string) => {
			queryClient.invalidateQueries({
				queryKey: ["/api/conversations", conversationId, "messages"],
			});
		};

		const getConversationId = (payload: Record<string, unknown>) => {
			const value = payload.conversationId;
			return typeof value === "string" ? value : null;
		};

		ws.onmessage = (event) => {
			try {
				const message = JSON.parse(event.data);

				if (handlerRef.current) {
					handlerRef.current(message);
				}

				switch (message.event) {
					case "conversation:created":
					case "conversation:updated":
						invalidateByPrefix("/api/conversations");
						invalidateByPrefix("/api/stats/dashboard");
						break;
					case "message:created": {
						invalidateByPrefix("/api/conversations");
						const conversationId = getConversationId(message.data);
						if (conversationId) {
							invalidateConversationMessages(conversationId);
						}
						break;
					}
					case "receipt:updated":
						invalidateByPrefix("/api/receipts");
						invalidateByPrefix("/api/stats/dashboard");
						break;
				}
			} catch (error) {
				console.error("WebSocket message parse error:", error);
			}
		};

		ws.onerror = (error) => {
			console.error("WebSocket error:", error);
		};

		ws.onclose = () => {
			console.log("WebSocket disconnected, reconnecting...");
			setTimeout(connect, 3000);
		};

		wsRef.current = ws;
	}, [currentProject?.id]);

	useEffect(() => {
		connect();

		return () => {
			if (wsRef.current) {
				wsRef.current.close();
			}
		};
	}, [connect]);

	return wsRef.current;
}
