{"output":"import { QueryClient, type QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
	if (!res.ok) {
		const text = (await res.text()) || res.statusText;
		throw new Error(`${res.status}: ${text}`);
	}
}

// CHANGE: constrain apiRequest method signature to HTTP verbs instead of an `any`-derived union
// WHY: fetch overload mismatch (TS2769) halted `npm run lint`
// QUOTE(T–ó): "–í–µ—Ä–∏—Ñ–∏–∫–∞—Ü–∏—è: `npm run lint` –¥–æ–ª–∂–Ω—ã –ø—Ä–æ—Ö–æ–¥–∏—Ç—å."
// REF: MSG-20250215-02
type HttpMethod = Exclude<RequestInit["method"], undefined>;

export async function apiRequest(
	method: HttpMethod,
	url: string,
	data?: unknown,
): Promise<Response> {
	const res = await fetch(url, {
		method,
		headers: data ? { "Content-Type": "application/json" } : {},
		body: data ? JSON.stringify(data) : undefined,
		credentials: "include",
	});

	await throwIfResNotOk(res);
	return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
	on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
	({ on401: unauthorizedBehavior }) =>
	async ({ queryKey }) => {
		const res = await fetch(queryKey.join("/") as string, {
			credentials: "include",
		});

		if (unauthorizedBehavior === "returnNull" && res.status === 401) {
			return null;
		}

		await throwIfResNotOk(res);
		return await res.json();
	};

export const queryClient = new QueryClient({
	defaultOptions: {
		queries: {
			queryFn: getQueryFn({ on401: "throw" }),
			staleTime: 30 * 1000, // 30 seconds - show cached data instantly
			gcTime: 5 * 60 * 1000, // 5 minutes - keep in memory
			refetchOnMount: false, // don't refetch if data is fresh
			refetchInterval: false,
			refetchOnWindowFocus: false,
			retry: false,
		},
		mutations: {
			retry: false,
		},
	},
});
