+type ContactPlatform = Contact["platform"];
+type AudiencePlatform = "all" | ContactPlatform;
+
+interface BroadcastTargetAudience {
+	platform: AudiencePlatform;
+	tags?: string[];
+	contactIds?: string[];
+	segmentId?: string;
+}
+
+const CONTACT_PLATFORMS: readonly ContactPlatform[] = ["telegram", "vk"];
+const AUDIENCE_TYPES: readonly AudienceType[] = [
+	"all",
+	"platform",
+	"tags",
+	"segment",
+	"specific",
+];
+
+// CHANGE: normalize broadcast target audience typing instead of relying on `any`
+// WHY: ESLint flagged every cast/mutation of audience payload because it used `any`
+// QUOTE(T–ó): "Unexpected any. Specify a different type."
+// REF: REQ-LINT-001
+// SOURCE: https://typescript-eslint.io/rules/no-explicit-any/
+const isAudiencePlatform = (value: unknown): value is AudiencePlatform =>
+	typeof value === "string" &&
+	(value === "all" || CONTACT_PLATFORMS.includes(value as ContactPlatform));
+
+const isAudienceType = (value: string): value is AudienceType =>
+	AUDIENCE_TYPES.includes(value as AudienceType);
+
+const extractStringArray = (value: unknown): string[] | undefined => {
+	if (!Array.isArray(value)) {
+		return undefined;
+	}
+	const items = value.filter((item): item is string => typeof item === "string");
+	return items.length > 0 ? items : undefined;
+};
+
+const ensureTargetAudience = (raw: unknown): BroadcastTargetAudience => {
+	if (!raw || typeof raw !== "object") {
+		return { platform: "all" };
+	}
+
+	const candidate = raw as Record<string, unknown>;
+	const platform = isAudiencePlatform(candidate.platform)
+		? candidate.platform
+		: "all";
+
+	const tags = extractStringArray(candidate.tags);
+	const contactIds = extractStringArray(candidate.contactIds);
+	const segmentId =
+		typeof candidate.segmentId === "string" ? candidate.segmentId : undefined;
+
+	return {
+		platform,
+		...(tags ? { tags } : {}),
+		...(contactIds ? { contactIds } : {}),
+		...(segmentId ? { segmentId } : {}),
+	};
+};
