  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(schema.users).where(eq(schema.users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(schema.users).where(eq(schema.users.email, email));
    return user || undefined;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(schema.users)
      .values(userData)
      .onConflictDoUpdate({
        target: schema.users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return requireRow(user, "[Storage] Failed to upsert user");
  }

  // Phase 0.5: User-Project operations (multi-tenancy)
  async getUserProjects(userId: string): Promise<UserProject[]> {
    return await db
      .select()
      .from(schema.userProjects)
      .where(eq(schema.userProjects.userId, userId))
      .orderBy(desc(schema.userProjects.createdAt));
  }

  async getUserAccessibleProjects(userId: string): Promise<Project[]> {
    const results = await db
      .select({
        id: schema.projects.id,
        name: schema.projects.name,
        description: schema.projects.description,
        deeplApiKey: schema.projects.deeplApiKey,
        createdAt: schema.projects.createdAt,
        updatedAt: schema.projects.updatedAt,
      })
      .from(schema.userProjects)
      .innerJoin(schema.projects, eq(schema.userProjects.projectId, schema.projects.id))
      .where(eq(schema.userProjects.userId, userId))
      .orderBy(desc(schema.projects.createdAt));
    return results;
  }

  async getProjectUsers(projectId: string): Promise<UserProject[]> {
    return await db
      .select()
      .from(schema.userProjects)
      .where(eq(schema.userProjects.projectId, projectId))
      .orderBy(desc(schema.userProjects.createdAt));
