+import { Pool, neonConfig } from "@neondatabase/serverless";
+import { drizzle as drizzleNeon } from "drizzle-orm/neon-serverless";
+import type { NeonDatabase } from "drizzle-orm/neon-serverless";
+import { drizzle as drizzlePglite } from "drizzle-orm/pglite";
+import type { PgliteDatabase } from "drizzle-orm/pglite";
+import { migrate } from "drizzle-orm/pglite/migrator";
+import { PGlite } from "@electric-sql/pglite";
+import ws from "ws";
+import * as schema from "@shared/schema";
+import { dirname, resolve } from "node:path";
+import { mkdirSync } from "node:fs";
+import { fileURLToPath } from "node:url";
+
+neonConfig.webSocketConstructor = ws;
+
+const moduleDirectory = dirname(fileURLToPath(import.meta.url));
+const projectRoot = resolve(moduleDirectory, "..");
+const defaultMigrationsFolder = resolve(projectRoot, "migrations");
+const defaultLocalDataDir = resolve(projectRoot, ".local-dev-db");
+
+function ensureDirectory(path: string): void {
+  mkdirSync(path, { recursive: true });
+}
+
+export interface DatabaseBootstrapOptions {
+  databaseUrl?: string;
+  nodeEnv: string;
+  migrationsFolder: string;
+  localDataDir: string;
+}
+
+export type DatabaseBootstrapResult =
+  | {
+      mode: "neon";
+      db: NeonDatabase<typeof schema>;
+      pool: Pool;
+    }
+  | {
+      mode: "pglite";
+      db: PgliteDatabase<typeof schema>;
+      engine: PGlite;
+    };
+
+// CHANGE: Added bootstrapDatabase to fall back to embedded PGlite (with Drizzle migrations) when DATABASE_URL is absent.
+// WHY: Local developers could not run `npm run dev` because the process threw before Vite booted, violating the invariant that development must not require external secrets.
+// QUOTE(T–ó): "–ú–æ–∂–µ—à—å –∏—Å–ø—Ä–∞–≤–∏—Ç—å —á—Ç–æ –±—ã —è –º–æ–≥ –∑–∞–ø—É—Å–∫–∞—Ç—å –ª–æ–∫–∞–ª—å–Ω–æ?"
+// REF: user-msg-2025-02-19-1
+// SOURCE: https://raw.githubusercontent.com/electric-sql/pglite/main/README.md ‚Äî "const db = new PGlite(\"./path/to/pgdata\");"
+export async function bootstrapDatabase(
+  options: DatabaseBootstrapOptions
+): Promise<DatabaseBootstrapResult> {
+  const { databaseUrl, nodeEnv, migrationsFolder, localDataDir } = options;
+
+  if (databaseUrl) {
+    const pool = new Pool({ connectionString: databaseUrl });
+    const db = drizzleNeon({ client: pool, schema });
+    return { mode: "neon", db, pool };
+  }
+
+  if (nodeEnv === "production") {
+    throw new Error("DATABASE_URL must be set. Did you forget to provision a database?");
+  }
+
+  ensureDirectory(localDataDir);
+  const engine = new PGlite(localDataDir);
+  const db = drizzlePglite(engine, { schema });
+  await migrate(db, { migrationsFolder });
+  return { mode: "pglite", db, engine };
+}
+
+const bootstrapResult = await bootstrapDatabase({
+  databaseUrl: process.env["DATABASE_URL"],
+  nodeEnv: process.env["NODE_ENV"] ?? "development",
+  migrationsFolder: defaultMigrationsFolder,
+  localDataDir: defaultLocalDataDir,
+});
+
+export const db = bootstrapResult.db;
+export const pool: Pool | undefined =
+  bootstrapResult.mode === "neon" ? bootstrapResult.pool : undefined;
+export const databaseMode = bootstrapResult.mode;
