const moduleDirectory = dirname(fileURLToPath(import.meta.url));
const projectRoot = resolve(moduleDirectory, "..");
const defaultMigrationsFolder = resolve(projectRoot, "migrations");
const defaultLocalDataDir = resolve(projectRoot, ".local-dev-db");

function ensureDirectory(path: string): void {
  mkdirSync(path, { recursive: true });
}

export interface DatabaseBootstrapOptions {
  databaseUrl?: string;
  nodeEnv: string;
  migrationsFolder: string;
  localDataDir: string;
}

export type DatabaseBootstrapResult =
  | {
      mode: "neon";
      db: NeonDatabase<typeof schema>;
      pool: Pool;
    }
  | {
      mode: "pglite";
      db: PgliteDatabase<typeof schema>;
      engine: PGlite;
    };

// CHANGE: Added bootstrapDatabase to fall back to embedded PGlite (with Drizzle migrations) when DATABASE_URL is absent.
// WHY: Local developers could not run `npm run dev` because the process threw before Vite booted, violating the invariant that development must not require external secrets.
// QUOTE(T–ó): "–ú–æ–∂–µ—à—å –∏—Å–ø—Ä–∞–≤–∏—Ç—å —á—Ç–æ –±—ã —è –º–æ–≥ –∑–∞–ø—É—Å–∫–∞—Ç—å –ª–æ–∫–∞–ª—å–Ω–æ?"
