export async function bootstrapDatabase(
  options: DatabaseBootstrapOptions
): Promise<DatabaseBootstrapResult> {
  const { databaseUrl, nodeEnv, migrationsFolder, localDataDir } = options;

  if (databaseUrl) {
    const pool = new Pool({ connectionString: databaseUrl });
    const db = drizzleNeon({ client: pool, schema });
    return { mode: "neon", db, pool };
  }

  if (nodeEnv === "production") {
    throw new Error("DATABASE_URL must be set. Did you forget to provision a database?");
  }

  ensureDirectory(localDataDir);
  const engine = new PGlite(localDataDir, {
    extensions: {
      vector: vectorExtension,
    },
  });
  await engine.waitReady;
  await engine.exec("CREATE EXTENSION IF NOT EXISTS vector");
  const db = drizzlePglite(engine, { schema });
  await migrate(db, { migrationsFolder });
  return { mode: "pglite", db, engine };
}

const bootstrapResult = await bootstrapDatabase({
  databaseUrl: process.env["DATABASE_URL"],
  nodeEnv: process.env["NODE_ENV"] ?? "development",
  migrationsFolder: defaultMigrationsFolder,
  localDataDir: defaultLocalDataDir,
});

export const db = bootstrapResult.db;
export const pool: Pool | undefined =
  bootstrapResult.mode === "neon" ? bootstrapResult.pool : undefined;
export const databaseMode = bootstrapResult.mode;
