  private async ensureVkContact(
    projectId: string,
    vkUserId: string
  ): Promise<{ record: Contact; created: boolean }> {
    let contact = await this.storage.getContactByPlatformUserId("vk", vkUserId, projectId);
    if (contact) {
      return { record: contact, created: false };
    }

    let firstName = "";
    let lastName = "";
    try {
      const userInfo = await this.getUserInfo(Number(vkUserId));
      firstName = userInfo.first_name ?? "";
      lastName = userInfo.last_name ?? "";
    } catch (error) {
      console.warn(`[VK] Failed to fetch user info for ${vkUserId}:`, error);
    }

    let created = false;
    try {
      contact = await this.storage.createContact({
        projectId,
        platform: "vk",
        platformUserId: vkUserId,
        firstName,
        lastName,
        tags: [],
      });
      created = true;
    } catch (error) {
      if (!this.isUniqueViolation(error)) {
        throw error;
      }
      contact = await this.storage.getContactByPlatformUserId("vk", vkUserId, projectId);
    }

    if (!contact) {
      throw new Error(`[VK] Failed to upsert contact for peer ${vkUserId}`);
    }

    if (created) {
      broadcastWebSocketEvent({
        type: "contact:created",
        data: contact,
      });
    }

    return { record: contact, created };
  }

