import type { VKBotService } from "./vk";

export async function resumeVkImports(storage: IStorage, vkService: VKBotService): Promise<void> {
  try {
    console.log("[VK Import Resumer] Checking for pending VK import jobs...");

    const jobs = await storage.getAllRunningVkImportJobs();

    if (jobs.length === 0) {
      console.log("[VK Import Resumer] No pending jobs found");
      return;
    }

    console.log(`[VK Import Resumer] Found ${jobs.length} pending job(s), auto-resuming...`);

    for (const job of jobs) {
      console.log(`[VK Import Resumer] Resuming job ${job.id} for project ${job.projectId} (status: ${job.status}, offset: ${job.currentOffset})`);

      const vkSettings = await storage.getVkSettings(job.projectId);
      if (!vkSettings?.accessToken || !vkSettings?.isActive) {
        console.warn(`[VK Import Resumer] Skipping job ${job.id} - VK not configured for project ${job.projectId}`);
        await storage.updateVkImportJob(job.id, {
          status: "failed",
          errorMessage: "VK credentials not configured or inactive",
          completedAt: new Date()
        });
        continue;
      }

      vkService.importAllConversations(job.projectId, job.id).catch((error) => {
        console.error(`[VK Import Resumer] Failed to resume job ${job.id}:`, error.message);
      });
    }

    console.log("[VK Import Resumer] All pending jobs resumed");
  } catch (error) {
    console.error("[VK Import Resumer] Error during resume:", error);
  }
}
