// SOURCE: n/a
import { randomBytes } from "crypto";
import { redisService } from "./redis";

function generateLockToken(): string {
  return randomBytes(16).toString("hex");
}

export interface WorkerLockHandle {
  token: string;
}

export interface WorkerLockAdapter {
  acquire(key: string, ttlMs: number): Promise<WorkerLockHandle | null>;
  release(key: string, handle: WorkerLockHandle): Promise<void>;
}

// CHANGE: WorkerLock adapters centralize mutual exclusion so VK sync can keep a single active worker across pods (REQ-VK-WORKER-RECOVERY).
// WHY: The previous in-memory flag guarded only one process; Redis-backed leases uphold the invariant "один воркер синхронизирует сообщения".
// QUOTE(TЗ): "нужна дополнительная защита от параллельного запуска самого worker'а"
// REF: REQ-VK-WORKER-RECOVERY
// SOURCE: n/a
class RedisWorkerLock implements WorkerLockAdapter {
  constructor(private readonly namespace: string) {}

  async acquire(key: string, ttlMs: number): Promise<WorkerLockHandle | null> {
    const token = generateLockToken();
    const ttlSeconds = Math.max(1, Math.ceil(ttlMs / 1000));
    const acquired = await redisService.acquireLock(this.namespace, key, token, ttlSeconds);
    if (!acquired) {
      return null;
    }
    return { token };
  }

  async release(key: string, handle: WorkerLockHandle): Promise<void> {
    await redisService.releaseLock(this.namespace, key, handle.token);
  }
}

class InMemoryWorkerLock implements WorkerLockAdapter {
  private readonly locks = new Map<string, string>();

  async acquire(key: string, _ttlMs: number): Promise<WorkerLockHandle | null> {
    const existing = this.locks.get(key);
    if (existing) {
      return null;
    }
    const token = generateLockToken();
    this.locks.set(key, token);
    return { token };
  }

  async release(key: string, handle: WorkerLockHandle): Promise<void> {
    const current = this.locks.get(key);
    if (current === handle.token) {
      this.locks.delete(key);
    }
  }
}

export function createInMemoryWorkerLock(): WorkerLockAdapter {
  return new InMemoryWorkerLock();
}

export function createWorkerLock(): WorkerLockAdapter {
  if (redisService.isAvailable()) {
    return new RedisWorkerLock("worker-lock");
  }
  return createInMemoryWorkerLock();
}
