*** Add File: src/shell/routes/verification.ts
+import { randomBytes, randomInt } from "node:crypto";
+
+import { and, desc, eq, gt, isNull } from "drizzle-orm";
+import type { Request, RequestHandler, Response } from "express";
+import { match } from "ts-pattern";
+
+import {
+	computeExpiresAt,
+	evaluateAttempt,
+	hashVerificationToken,
+	isRateLimited,
+	type VerificationAttemptOutcome,
+} from "../../core/verification.js";
+import {
+	verificationCodes,
+	type VerificationCodeRow,
+	users,
+} from "../../core/schema/index.js";
+import { db } from "../db.js";
+
+type VerificationErrorCode =
+	| "code_invalid"
+	| "code_expired"
+	| "too_many_attempts"
+	| "rate_limited";
+
+type VerificationType = VerificationCodeRow["type"];
+
+type VerificationRequestResponse = {
+	readonly status: "ok";
+	readonly alreadyVerified?: boolean;
+	readonly emailVerified?: boolean;
+	readonly phoneVerified?: boolean;
+	readonly devVerifyUrl?: string;
+	readonly devCode?: string;
+};
+
+const now = (): Date => new Date();
+
+const asPositiveInt = (value: string | undefined, fallback: number): number => {
+	const numeric = Number(value);
+	return Number.isInteger(numeric) && numeric > 0 ? numeric : fallback;
+};
+
+const config = {
+	emailTtlMinutes: asPositiveInt(process.env.EMAIL_VERIFICATION_TTL_MIN, 60),
+	phoneTtlMinutes: asPositiveInt(process.env.PHONE_VERIFICATION_TTL_MIN, 10),
+	requestsPerHour: asPositiveInt(
+		process.env.VERIFICATION_REQUEST_RATE_LIMIT_PER_HOUR,
+		5,
+	),
+	phoneMaxAttempts: asPositiveInt(
+		process.env.PHONE_VERIFICATION_MAX_ATTEMPTS,
+		5,
+	),
+	devMode:
+		(process.env.VERIFICATION_DEV_MODE ?? "false").toLowerCase() === "true",
+	appUrl: process.env.APP_URL ?? "",
+	fromEmail: process.env.RESEND_FROM_EMAIL ?? "",
+	resendApiKey: process.env.RESEND_API_KEY ?? "",
+};
+
+const verificationMessages: Record<VerificationErrorCode, string> = {
+	code_invalid: "Verification token or code is invalid or already used",
+	code_expired: "Verification token or code expired",
+	too_many_attempts: "Maximum verification attempts exceeded",
+	rate_limited: "Too many verification requests in the last hour",
+};
+
+const respondVerificationError = (
+	res: Response,
+	status: number,
+	code: VerificationErrorCode,
+) => {
+	res.status(status).json({
+		error: {
+			code,
+			message: verificationMessages[code],
+		},
+	});
+	return null;
+};
+
+const ensureAuthenticated = (req: Request, res: Response): string | null => {
+	const { userId } = req.session;
+	if (!userId) {
+		res.status(401).json({ error: "unauthorized" });
+		return null;
+	}
+	return userId;
+};
+
+const findUserById = (id: string) =>
+	db.query.users.findFirst({
+		where: (tbl, { eq: eqFn }) => eqFn(tbl.id, id),
+	});
+
+const countRecentRequests = (userId: string, type: VerificationType) => {
+	const cutoff = new Date(now().getTime() - 60 * 60 * 1000);
+	return db
+		.select({ value: db.raw<number>("count(*)") })
+		.from(verificationCodes)
+		.where(
+			and(
+				eq(verificationCodes.userId, userId),
+				eq(verificationCodes.type, type),
+				gt(verificationCodes.createdAt, cutoff),
+			),
+		)
+		.then(([row]) => Number(row?.value ?? 0));
+};
+
+const generateEmailToken = () => {
+	const token = randomBytes(32).toString("hex");
+	return { token, tokenHash: hashVerificationToken(token) };
+};
+
+const generatePhoneCode = () => {
+	const code = randomInt(100_000, 1_000_000).toString();
+	return { code, codeHash: hashVerificationToken(code) };
+};
+
+const deactivateActiveCodes = (userId: string, type: VerificationType) =>
+	db
+		.delete(verificationCodes)
+		.where(
+			and(
+				eq(verificationCodes.userId, userId),
+				eq(verificationCodes.type, type),
+				isNull(verificationCodes.usedAt),
+			),
+		);
+
+const insertVerificationCode = (
+	params: Omit<
+		VerificationCodeRow,
+		"id" | "createdAt" | "usedAt" | "attempts"
+	> & { readonly attempts?: number },
+) =>
+	db
+		.insert(verificationCodes)
+		.values({
+			...params,
+			attempts: params.attempts ?? 0,
+		})
+		.returning()
+		.then(([row]) => row ?? null);
+
+const sendEmailVerification = (
+	to: string,
+	verifyUrl: string,
+): Promise<void> => {
+	if (!config.resendApiKey || !config.fromEmail) {
+		return Promise.resolve();
+	}
+	return fetch("https://api.resend.com/emails", {
+		method: "POST",
+		headers: {
+			Authorization: `Bearer ${config.resendApiKey}`,
+			"Content-Type": "application/json",
+		},
+		body: JSON.stringify({
+			from: config.fromEmail,
+			to,
+			subject: "Email verification",
+			html: `<p>–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ email: <a href="${verifyUrl}">${verifyUrl}</a></p>`,
+			text: `–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ email: ${verifyUrl}`,
+		}),
+	}).then(() => undefined);
+};
+
+const updateAttempts = (id: string, attempts: number) =>
+	db
+		.update(verificationCodes)
+		.set({ attempts })
+		.where(eq(verificationCodes.id, id));
+
+const markEmailVerified = (code: VerificationCodeRow, timestamp: Date) =>
+	db.transaction((tx) =>
+		tx
+			.update(users)
+			.set({ emailVerifiedAt: timestamp })
+			.where(eq(users.id, code.userId))
+			.then(() =>
+				tx
+					.update(verificationCodes)
+					.set({ usedAt: timestamp })
+					.where(eq(verificationCodes.id, code.id)),
+			),
+	);
+
+const markPhoneVerified = (code: VerificationCodeRow, timestamp: Date) =>
+	db.transaction((tx) =>
+		tx
+			.update(users)
+			.set({ phoneVerifiedAt: timestamp })
+			.where(eq(users.id, code.userId))
+			.then(() =>
+				tx
+					.update(verificationCodes)
+					.set({ usedAt: timestamp })
+					.where(eq(verificationCodes.id, code.id)),
+			),
+	);
+
+const evaluateOutcome = (
+	code: VerificationCodeRow,
+	providedHash: string,
+): VerificationAttemptOutcome =>
+	evaluateAttempt({
+		storedHash: code.tokenHash,
+		providedHash,
+		attempts: code.attempts ?? 0,
+		maxAttempts: code.maxAttempts,
+		expiresAt: code.expiresAt,
+		usedAt: code.usedAt ?? null,
+		now: now(),
+	});
+
+const ensureRateLimit = (
+	userId: string,
+	type: VerificationType,
+	res: Response,
+) =>
+	countRecentRequests(userId, type).then((recent) =>
+		isRateLimited(recent, config.requestsPerHour)
+			? respondVerificationError(res, 429, "rate_limited")
+			: recent,
+	);
+
+const ensureUserExists = (
+	userPromise: Promise<typeof users.$inferSelect | undefined>,
+	res: Response,
+) =>
+	userPromise.then((user) => {
+		if (!user) {
+			res.status(404).json({ error: "user_not_found" });
+			return null;
+		}
+		return user;
+	});
+
+export const requestEmailVerification: RequestHandler = (req, res, next) => {
+	const userId = ensureAuthenticated(req, res);
+	if (!userId) {
+		return;
+	}
+	ensureUserExists(findUserById(userId), res)
+		.then((user) => {
+			if (!user) {
+				return null;
+			}
+			if (user.emailVerifiedAt) {
+				res.status(200).json({
+					status: "ok",
+					alreadyVerified: true,
+					emailVerified: true,
+				});
+				return null;
+			}
+			return ensureRateLimit(user.id, "email", res).then((allowed) => {
+				if (allowed === null) {
+					return null;
+				}
+				const { token, tokenHash } = generateEmailToken();
+				const expiresAt = computeExpiresAt(now().getTime(), config.emailTtlMinutes);
+				const verifyUrl = `${config.appUrl}/auth/email/verify?token=${encodeURIComponent(token)}`;
+				return deactivateActiveCodes(user.id, "email")
+					.then(() =>
+						insertVerificationCode({
+							userId: user.id,
+							type: "email",
+							sentTo: user.email,
+							tokenHash,
+							expiresAt,
+							maxAttempts: 1,
+						}),
+					)
+					.then(() => sendEmailVerification(user.email, verifyUrl))
+					.then(() => {
+						const payload: VerificationRequestResponse = { status: "ok" };
+						if (config.devMode) {
+							payload.devVerifyUrl = verifyUrl;
+						}
+						res.status(200).json(payload);
+						return null;
+					});
+			});
+		})
+		.catch(next);
+};
+
+export const confirmEmailVerification: RequestHandler = (req, res, next) => {
+	const { token } = req.body as { readonly token?: unknown };
+	if (typeof token !== "string" || token.trim().length === 0) {
+		res.status(400).json({ error: "invalid_payload" });
+		return;
+	}
+	const tokenHash = hashVerificationToken(token);
+	db.query.verificationCodes
+		.findFirst({
+			where: (tbl, { and: andFn, eq: eqFn, isNull: isNullFn }) =>
+				andFn(
+					eqFn(tbl.tokenHash, tokenHash),
+					eqFn(tbl.type, "email"),
+					isNullFn(tbl.usedAt),
+				),
+		})
+		.then((record) => {
+			if (!record) {
+				return respondVerificationError(res, 400, "code_invalid");
+			}
+			const outcome = evaluateOutcome(record, tokenHash);
+			return match(outcome)
+				.with({ _tag: "Expired" }, () =>
+					respondVerificationError(res, 400, "code_expired"),
+				)
+				.with({ _tag: "TooManyAttempts" }, () =>
+					respondVerificationError(res, 400, "too_many_attempts"),
+				)
+				.with({ _tag: "Invalid" }, (invalid) =>
+					updateAttempts(record.id, invalid.nextAttempts).then(() =>
+						invalid.nextAttempts >= record.maxAttempts
+							? respondVerificationError(res, 400, "too_many_attempts")
+							: respondVerificationError(res, 400, "code_invalid"),
+					),
+				)
+				.with({ _tag: "Verified" }, () =>
+					markEmailVerified(record, now()).then(() =>
+						res.status(200).json({ status: "ok", emailVerified: true }),
+					),
+				)
+				.exhaustive();
+		})
+		.catch(next);
+};
+
+export const requestPhoneVerification: RequestHandler = (req, res, next) => {
+	const userId = ensureAuthenticated(req, res);
+	if (!userId) {
+		return;
+	}
+	ensureUserExists(findUserById(userId), res)
+		.then((user) => {
+			if (!user) {
+				return null;
+			}
+			if (user.phoneVerifiedAt) {
+				res.status(200).json({
