const generatePhoneCode = () => {
	const randomValue = randomBytes(4).readUInt32BE(0);
	const sixDigit = (randomValue % 900_000) + 100_000;
	const code = sixDigit.toString().padStart(6, "0");
	return { code, codeHash: hashVerificationToken(code) };
};

