import bcrypt from "bcryptjs";
import type { NextFunction, Request, RequestHandler, Response } from "express";
import * as Effect from "effect/Effect";
import { pipe } from "effect/Function";

import { users } from "../../core/schema/index.js";
import { db } from "../db.js";
import { type LoginCandidate, parseLogin } from "./auth-helpers.js";

export const isAuthenticated = (
	req: Request,
	res: Response,
	next: NextFunction,
): void => {
	if (req.session.userId) {
		next();
		return;
	}
	res.status(401).json({ error: "unauthorized" });
};

const respondError = (res: Response, status: number, error: string): null => {
	res.status(status).json({ error });
	return null;
};

type UserRow = typeof users.$inferSelect;
type DbError = { readonly _tag: "DbError"; readonly cause: unknown };
type AuthError =
	| { readonly _tag: "InvalidCredentials" }
	| { readonly _tag: "UserNotFound" }
	| DbError;

const toUserPayload = (user: UserRow): {
	readonly id: string;
	readonly email: string;
	readonly firstName: string;
	readonly lastName: string;
	readonly profileImageUrl: string | null;
	readonly emailVerified: boolean;
	readonly phoneVerified: boolean;
} => ({
	id: user.id,
	email: user.email,
	firstName: user.firstName,
	lastName: user.lastName,
	profileImageUrl: user.profileImageUrl ?? null,
	emailVerified: user.emailVerifiedAt !== null,
	phoneVerified: user.phoneVerifiedAt !== null,
});

const verifyPassword = (password: string, passwordHash: string): boolean =>
	bcrypt.compareSync(password, passwordHash);

const asDbError = (cause: unknown): DbError => ({ _tag: "DbError", cause });

const findUserByEmail = (
	email: string,
): Effect.Effect<UserRow | undefined, DbError> =>
	Effect.tryPromise({
		try: () =>
			db.query.users.findFirst({
				where: (tbl, { eq: eqFn }) => eqFn(tbl.email, email),
			}),
		catch: asDbError,
	});

const findUserById = (id: string): Effect.Effect<UserRow | undefined, DbError> =>
	Effect.tryPromise({
		try: () =>
			db.query.users.findFirst({
				where: (tbl, { eq: eqFn }) => eqFn(tbl.id, id),
			}),
		catch: asDbError,
	});

export const postLogin: RequestHandler = (req, res, next) => {
	const payload = parseLogin(req.body as LoginCandidate);
	if (!payload) {
		res.status(400).json({ error: "invalid_payload" });
		return;
	}
	const program = pipe(
		findUserByEmail(payload.email.toLowerCase()),
		Effect.flatMap((user) =>
			user
				? verifyPassword(payload.password, user.passwordHash)
					? Effect.sync(() => {
							req.session.userId = user.id;
							res.json(toUserPayload(user));
						})
					: Effect.sync(() => respondError(res, 401, "invalid_credentials"))
				: Effect.sync(() => respondError(res, 401, "invalid_credentials")),
		),
		Effect.catchAll((err) =>
			Effect.sync(() => {
				next(err as unknown as Error);
			}),
		),
	);
	Effect.runPromise(program).catch(next);
};

export const postLogout: RequestHandler = (req, res) => {
	req.session.destroy((err) => {
		if (err) {
			res.status(500).json({ error: "logout_failed" });
			return;
		}
		res.status(204).end();
	});
};

export const getMe: RequestHandler = (req, res, next) => {
	if (!req.session.userId) {
		res.status(401).json({ error: "unauthorized" });
		return;
	}
	const program = pipe(
		findUserById(req.session.userId),
		Effect.flatMap((user) =>
			user
				? Effect.sync(() => {
						res.json(toUserPayload(user));
					})
				: Effect.sync(() => {
						res.status(404).json({ error: "user_not_found" });
					}),
		),
		Effect.catchAll((err) =>
			Effect.sync(() => {
				next(err as unknown as Error);
			}),
		),
	);
	Effect.runPromise(program).catch(next);
};

