    return;
  }
  elements.inviteSuccessToken.textContent = latest.token;
  elements.inviteSuccessExpiry.textContent = `Expires at ${formatDate(latest.expiresAt)}`;
  elements.inviteSuccess.classList.remove("hidden");
};

