export const RegisterApp: FC = (): ReactElement => {
  const [form, setForm] = useState<FormState>({
    token: readPrefilledToken(),
    password: "",
  });
  const [submitting, setSubmitting] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const { toast, push } = useToast();

  const canSubmit = useMemo(
    () => form.token.trim().length > 0 && form.password.trim().length > 0,
    [form.password, form.token],
  );

  useEffect(() => {
    const token = readPrefilledToken();
    if (token.length > 0) {
      setForm((prev) => ({ ...prev, token }));
    }
  }, []);

  const handleChange =
    (key: keyof FormState) => (event: ChangeEvent<HTMLInputElement>) => {
      setForm((prev) => ({ ...prev, [key]: event.target.value }));
    };

  const redirectAfterSuccess = (): void => {
    window.setTimeout(() => {
      window.location.href = "/";
    }, 900);
  };

  const handleSubmit = (event: FormEvent<HTMLFormElement>): void => {
    event.preventDefault();
    if (!canSubmit) {
      setError("Token and password are required.");
      return;
    }
    setSubmitting(true);
    setError(null);
    setSuccess(null);
    void Effect.runPromise(
      registerWithInvitation({
        token: form.token.trim(),
        password: form.password,
