type RegisterViewModel = {
  readonly form: FormState;
  readonly setField: (key: keyof FormState, value: string) => void;
  readonly submitting: boolean;
  readonly error: string | null;
  readonly success: string | null;
  readonly toast: ToastState | null;
  readonly canSubmit: boolean;
  readonly handleSubmit: (event: FormEvent<HTMLFormElement>) => void;
};

const useRegisterView = (): RegisterViewModel => {
  const [form, setForm] = useState<FormState>({
    token: readPrefilledToken(),
    password: "",
  });
  const [submitting, setSubmitting] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const { toast, push } = useToast();

  const canSubmit = useMemo(
    () => form.token.trim().length > 0 && form.password.trim().length > 0,
    [form.password, form.token],
  );

  useEffect(() => {
    const token = readPrefilledToken();
    if (token.length > 0) {
      setForm((prev) => ({ ...prev, token }));
    }
  }, []);

  const setField = useCallback(
    (key: keyof FormState, value: string): void => {
      setForm((prev) => ({ ...prev, [key]: value }));
    },
    [],
  );

  const redirectAfterSuccess = useCallback((): void => {
    window.setTimeout(() => {
      window.location.href = "/";
    }, 900);
  }, []);

  const handleSubmit = useCallback(
    (event: FormEvent<HTMLFormElement>): void => {
      event.preventDefault();
      if (!canSubmit) {
        setError("Token and password are required.");
        return;
      }
      setSubmitting(true);
      setError(null);
      setSuccess(null);
      void Effect.runPromise(
        registerWithInvitation({
          token: form.token.trim(),
          password: form.password,
        }),
      )
        .then((result) => {
          setSubmitting(false);
          if (result._tag === "Success") {
            setSuccess("Registration successful. Redirecting‚Ä¶");
            push("Registration successful", "success");
            redirectAfterSuccess();
            return;
          }
          setError(result.error);
          push(result.error, "error");
        })
        .catch(() => {
          setSubmitting(false);
          setError("Unexpected error");
          push("Unexpected error", "error");
        });
    },
    [canSubmit, form.password, form.token, push, redirectAfterSuccess],
  );

  return {
    form,
    setField,
    submitting,
    error,
    success,
    toast,
    canSubmit,
    handleSubmit,
  };
};

export const RegisterApp: FC = (): ReactElement => {
  const {
    form,
    setField,
    submitting,
    error,
    success,
    toast,
    canSubmit,
    handleSubmit,
  } = useRegisterView();

