export function useAuth() {
	const fetchAuth = getQueryFn<AuthResponse | null>({ on401: "returnNull" });
	const unauthorizedRef = useRef(false);
	const hasCookie = hasSessionCookie();
	const renderCountRef = useRef(0);
	renderCountRef.current += 1;
	const renderCount = renderCountRef.current;

	logAuthState("auth state", {
		cookiePresent: hasCookie,
		cookieLength: 0,
		unauthorized: unauthorizedRef.current,
		renderCount,
	});

	const { data, isLoading, error, status, fetchStatus, isFetching } =
		useQuery<AuthResponse | null>({
			queryKey: ["/api/auth/user"],
			// CHANGE: –ù–µ –æ–ø—Ä–∞—à–∏–≤–∞–µ–º /api/auth/user –±–µ–∑ —Å–µ—Å—Å–∏–∏ –∏ –æ—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º—Å—è –ø–æ—Å–ª–µ 401
