			// QUOTE(T–ó): "–ü–æ—Å—Ç–æ—è–Ω–Ω–æ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—ã"
			// REF: user-msg-current
			enabled: hasCookie && !unauthorizedRef.current,
			queryFn: async (ctx) => {
				const result = await fetchAuth(ctx);
				if (!result?.user) {
					console.debug(
						"[useAuth] auth fetch returned empty, marking unauthorized until cookie changes",
					);
					unauthorizedRef.current = true;
					logAuthState("auth fetch empty -> mark unauthorized", {
						cookiePresent: hasCookie,
						cookieLength: 0,
						unauthorized: unauthorizedRef.current,
						renderCount,
					});
					return null;
				}
				logAuthState("auth fetch success", {
					cookiePresent: hasCookie,
					cookieLength: 0,
					unauthorized: unauthorizedRef.current,
					renderCount,
				});
				return result;
			},
			retry: false,
			staleTime: 1000 * 60 * 5,
			// CHANGE: –û—Ç–∫–ª—é—á–∞–µ–º —Ñ–æ–Ω–æ–≤—ã–µ refetch, –ø–æ–∫–∞ –Ω–µ—Ç –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
			refetchInterval: false,
			refetchOnWindowFocus: (query) => !!query.state.data?.user,
			refetchOnReconnect: (query) => !!query.state.data?.user,
		});

	logAuthState("query lifecycle snapshot", {
		cookiePresent: hasCookie,
		cookieLength: 0,
		unauthorized: unauthorizedRef.current,
		renderCount,
	});
	logAuthState(
		`query status=${status} fetchStatus=${fetchStatus} fetching=${isFetching}`,
		{
			cookiePresent: hasCookie,
			cookieLength: 0,
			unauthorized: unauthorizedRef.current,
			renderCount,
		},
	);

	const logout = useMutation({
		mutationFn: async () => {
			return apiRequest("POST", "/api/auth/logout");
		},
		onSuccess: () => {
			queryClient.clear();
			window.location.href = "/";
		},
	});

	const getDisplayName = () => {
		if (!data?.user) return "User";
		const { firstName, lastName, email } = data.user;
		if (firstName && lastName) return `${firstName} ${lastName}`;
		if (firstName) return firstName;
		if (lastName) return lastName;
		if (email) return email;
		return "User";
	};

	return {
		user: data?.user,
		projects: data?.projects || [],
		isLoading,
		isAuthenticated: !!data?.user,
		error,
		logout: logout.mutate,
		isLoggingOut: logout.isPending,
		getDisplayName,
	};
