import { QueryClient, type QueryFunction } from "@tanstack/react-query";

type JsonPrimitive = string | number | boolean | null | undefined;
type JsonBody =
	| JsonPrimitive
	| JsonBody[]
	| { [key: string]: JsonBody | undefined };
// CHANGE: scope request payloads to serializable JSON instead of `unknown`
// WHY: lint forbids `unknown`; apiRequest only transmits JSON bodies
// QUOTE(T–ó): "Don't use `unknown` as a type. –ù–µ –∏—Å–ø–æ–ª—å–∑—É–µ–º 'unknown'."
// REF: MSG-20250217-01
// SOURCE: REST endpoints accept JSON payloads only

async function throwIfResNotOk(res: Response) {
	if (!res.ok) {
		const text = (await res.text()) || res.statusText;
		throw new Error(`${res.status}: ${text}`);
	}
}

// CHANGE: constrain apiRequest method signature to HTTP verbs instead of an `any`-derived union
// WHY: fetch overload mismatch (TS2769) halted `npm run lint`
// QUOTE(T–ó): "–í–µ—Ä–∏—Ñ–∏–∫–∞—Ü–∏—è: `npm run lint` –¥–æ–ª–∂–Ω—ã –ø—Ä–æ—Ö–æ–¥–∏—Ç—å."
// REF: MSG-20250215-02
type HttpMethod = Exclude<RequestInit["method"], undefined>;

export async function apiRequest(
	method: HttpMethod,
	url: string,
	data?: JsonBody | object,
): Promise<Response> {
	// CHANGE: only attach fetch `body` when payload exists to appease exact optional property typing
	// WHY: passing `{ body: undefined }` caused TS2769 against `RequestInit`
	// QUOTE(T–ó): "Argument of type '{ ... body: string | undefined; ... }' is not assignable to parameter of type 'RequestInit'."
	// REF: REQ-LINT-001
	// SOURCE: https://www.typescriptlang.org/tsconfig#exactOptionalPropertyTypes
	const init: RequestInit = {
		method,
		headers: data ? { "Content-Type": "application/json" } : {},
		credentials: "include",
	};
	if (data !== undefined) {
		init.body = JSON.stringify(data);
	}

	const res = await fetch(url, init);

	await throwIfResNotOk(res);
	return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
	on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
	({ on401: unauthorizedBehavior }) =>
	async ({ queryKey }) => {
		const res = await fetch(queryKey.join("/") as string, {
			credentials: "include",
		});

		if (unauthorizedBehavior === "returnNull" && res.status === 401) {
			return null;
		}

		await throwIfResNotOk(res);
		return await res.json();
	};

export const queryClient = new QueryClient({
	defaultOptions: {
		queries: {
			queryFn: getQueryFn({ on401: "throw" }),
			staleTime: 30 * 1000, // 30 seconds - show cached data instantly
			gcTime: 5 * 60 * 1000, // 5 minutes - keep in memory
			refetchOnMount: false, // don't refetch if data is fresh
			refetchInterval: false,
			refetchOnWindowFocus: false,
			retry: false,
		},
		mutations: {
			retry: false,
		},
	},
});

