	"user_exists",
	"invitation_not_found_or_expired",
	"logout_failed",
	"user_creation_failed",
];

const isAuthErrorCode = (value: string): value is AuthErrorCode =>
	authErrorCodes.includes(value as AuthErrorCode);

const extractAuthErrorCode = (message: string): AuthErrorCode | null => {
	const trimmed = message.trim();
