import { QueryClient, type QueryFunction } from "@tanstack/react-query";

type JsonPrimitive = string | number | boolean | null;
type JsonBody =
  | JsonPrimitive
  | readonly JsonBody[]
  | { readonly [key: string]: JsonBody | undefined };

const throwIfResNotOk = async (res: Response): Promise<void> => {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
};

type HttpMethod = Exclude<RequestInit["method"], undefined>;

// CHANGE: Strictly typed JSON fetcher that always includes session credentials
// WHY: Auth endpoints rely on HttpOnly session cookies; omitting credentials breaks invariants
// QUOTE(–¢–ó): "–ö–ª–∏–µ–Ω—Ç—Å–∫–∏–π API-–∫–ª–∏–µ–Ω—Ç ... apiRequest –∏ getQueryFn —Å credentials: \"include\""
// REF: user-msg-current
// FORMAT THEOREM: ‚àÄres: ok(res) ‚Üí parseJson(res) succeeds
// PURITY: SHELL
// INVARIANT: Session cookies are forwarded on every request
export const apiRequest = async (
  method: HttpMethod,
  url: string,
  data?: JsonBody,
): Promise<Response> => {
  const init: RequestInit = {
    method,
    credentials: "include",
  };
  if (data !== undefined) {
    init.headers = { "content-type": "application/json" };
    init.body = JSON.stringify(data);
  }

  const res = await fetch(url, init);
  await throwIfResNotOk(res);
  return res;
};

type UnauthorizedBehavior = "returnNull" | "throw";

export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> = ({ on401 }) => async ({ queryKey, signal }) => {
  const target = typeof queryKey[0] === "string" ? queryKey[0] : String(queryKey[0]);
  const res = await fetch(target, {
    credentials: "include",
    signal,
  });

  if (on401 === "returnNull" && res.status === 401) {
    return null as T;
  }

  await throwIfResNotOk(res);
  return (await res.json()) as T;
};

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      staleTime: 5 * 60 * 1000,
      gcTime: 15 * 60 * 1000,
      refetchOnMount: false,
      refetchInterval: false,
      refetchOnWindowFocus: false,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

