import { type QueryFunctionContext, useMutation, useQuery } from "@tanstack/react-query";
import { Effect, pipe } from "effect";
import { useRef } from "react";

import { apiRequest, getQueryFn, queryClient } from "../lib/queryClient.js";

export interface AuthUser {
  readonly id: string;
  readonly email: string;
  readonly firstName: string;
  readonly lastName: string;
  readonly profileImageUrl: string | null;
  readonly emailVerified: boolean;
  readonly phoneVerified: boolean;
}

interface AuthLogContext {
  readonly cookiePresent: boolean;
  readonly unauthorized: boolean;
  readonly renderCount: number;
}

const hasSessionCookie = (): boolean => true;

const logAuthState = (message: string, state: AuthLogContext): void => {
  // CHANGE: Minimal diagnostic logging for auth state transitions
  // WHY: User requested explicit [useAuth] logs for debugging unauthorized/refetch loops
  // QUOTE(T–ó): "–ø–∏—à–µ—Ç –¥–∏–∞–≥–Ω–æ—Å—Ç–∏—á–µ—Å–∫–∏–µ –ª–æ–≥–∏ [useAuth], –æ—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ—Ç refetch –ø–æ—Å–ª–µ 401"
  // REF: user-msg-current
  // PURITY: SHELL
  console.debug("[useAuth]", message, {
    cookiePresent: state.cookiePresent,
    unauthorized: state.unauthorized,
    renderCount: state.renderCount,
  });
};

type UseAuthResult = {
  readonly user: AuthUser | null;
  readonly isLoading: boolean;
  readonly isAuthenticated: boolean;
  readonly error: Error | null;
  readonly logout: () => void;
  readonly isLoggingOut: boolean;
  readonly getDisplayName: () => string;
};

export const useAuth = (): UseAuthResult => {
  const fetchAuth = getQueryFn<AuthUser | null>({ on401: "returnNull" }) as (
    ctx: QueryFunctionContext,
  ) => Promise<AuthUser | null>;
  const unauthorizedRef = useRef(false);
  const hasCookie = hasSessionCookie();
  const renderCountRef = useRef(0);
  renderCountRef.current += 1;

  logAuthState("auth state", {
    cookiePresent: hasCookie,
    unauthorized: unauthorizedRef.current,
    renderCount: renderCountRef.current,
  });

  const { data, isLoading, error, status, fetchStatus, isFetching } = useQuery<AuthUser | null>({
    queryKey: ["/api/auth/me"],
    enabled: hasCookie && !unauthorizedRef.current,
    queryFn: (ctx) =>
      Effect.runPromise(
        pipe(
          Effect.tryPromise({
            try: () => fetchAuth(ctx),
            catch: (error) => (error instanceof Error ? error : new Error(String(error))),
          }),
          Effect.flatMap((result) => {
            if (!result) {
              unauthorizedRef.current = true;
              logAuthState("auth fetch empty -> mark unauthorized", {
                cookiePresent: hasCookie,
                unauthorized: unauthorizedRef.current,
                renderCount: renderCountRef.current,
              });
              return Effect.succeed<AuthUser | null>(null);
            }
            logAuthState("auth fetch success", {
              cookiePresent: hasCookie,
              unauthorized: unauthorizedRef.current,
              renderCount: renderCountRef.current,
            });
            return Effect.succeed(result);
          }),
        ),
      ),
    retry: false,
    staleTime: 5 * 60 * 1000,
    refetchInterval: false,
    refetchOnWindowFocus: (query) => query.state.data !== null,
    refetchOnReconnect: (query) => query.state.data !== null,
  });

  logAuthState(
    `query status=${status} fetchStatus=${fetchStatus} fetching=${isFetching}`,
    {
      cookiePresent: hasCookie,
      unauthorized: unauthorizedRef.current,
      renderCount: renderCountRef.current,
    },
  );

  const logout = useMutation({
    mutationFn: () => Effect.runPromise(apiRequest("POST", "/api/auth/logout")),
    onSuccess: () => {
      queryClient.clear();
      window.location.href = "/auth-admin";
    },
  });

  const getDisplayName = (): string => {
    const currentUser = data ?? null;
    if (!currentUser) return "User";
    if (currentUser.firstName && currentUser.lastName) {
      return `${currentUser.firstName} ${currentUser.lastName}`;
    }
    if (currentUser.firstName) return currentUser.firstName;
    if (currentUser.lastName) return currentUser.lastName;
    if (currentUser.email) return currentUser.email;
    return "User";
  };

  const user = data ?? null;

  return {
    user,
    isLoading,
    isAuthenticated: user !== null,
    error,
    logout: logout.mutate,
    isLoggingOut: logout.isPending,
    getDisplayName,
  };
};

